import React, { Component } from 'react';

class Post extends Component {
    static defaultProps = {
      showText: ''
    }
    handleTextClick () {
       if(this.props.onTextClick) {
          this.props.onTextClick(this.refs.p.style.height)
       }
    }
    render() {
        return (
           <p onClick={this.handleTextClick.bind(this)} ref='p'>
             {this.props.showText}
           </p>
        )
    }
}
