import React, { Component } from 'react';

class LikeButtonApp extends Component {
    constructor () {
        super()
        this.state = {
           isLike: true
        }
    }
    handleButtonClick () {
      this.setState({
          isLike: !this.state.isLike
      })
    }
    render () {
          return (
              <button onClick={this.handleButtonClick.bind(this)}>
                {this.state.isLike ? '喜欢' : '不喜欢'}
              </button>
          )
    }
}

export default LikeButtonApp
